import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './login_page.css';
import logo from './assets/logo2movfara2.png';

function LoginPage() {
  const [isRegistering, setIsRegistering] = useState(false);
  const navigate = useNavigate();

  const toggleForm = () => {
    setIsRegistering((prev) => !prev);
  };

  const handleSignIn = async (event) => {
  event.preventDefault();
  const email = event.target.email.value;
  const password = event.target.password.value;

  try {
    const response = await fetch("http://localhost:5000/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username: email, password }),
    });

    const data = await response.json();
    if (response.ok) {
      localStorage.setItem("token", data.token);
      navigate("/channel");
    } else {
      alert(data.error);
    }
  } catch (error) {
    console.error("Error during login:", error);
  }
};

const handleRegister = async (event) => {
  event.preventDefault();
  const email = event.target.email.value;
  const password = event.target.password.value;

  try {
    const response = await fetch("http://localhost:5000/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username: email, password }),
    });

    const data = await response.json();
    if (response.ok) {
      alert("Account created successfully!");
      navigate("/channel");
    } else {
      alert(data.error);
    }
  } catch (error) {
    console.error("Error during registration:", error);
  }
};


  return (
    <div className="login-container">
      <div className="login-card">
        {isRegistering ? (
          <div id="register-section" className="form-section active">
            <img src={logo} alt="Logo" className="logo" />
            <h2>Register</h2>
            <p>Create a new account</p>
            <form id="register-form" onSubmit={handleRegister}>
              <input type="text" id="register-username" placeholder="Username" required />
              <input type="email" id="register-email" placeholder="Email" required />
              <input
                type="password"
                id="register-password"
                placeholder="Choose a password"
                required
              />
              <button type="submit" className="register-button">
                Create Account
              </button>
            </form>
            <p>
              <button onClick={toggleForm} className="link-button">
                Already have an account? Sign in here
              </button>
            </p>
          </div>
        ) : (
          <div id="login-section" className="form-section active">
            <img src={logo} alt="Logo" className="logo" />
            <h2>Sign In</h2>
            <p>to continue to YouTube</p>
            <form id="login-form" onSubmit={handleSignIn}>
              <input type="email" id="login-email" placeholder="Email" required />
              <input type="password" id="login-password" placeholder="Enter your password" required />
              <button type="submit" className="login-button">
                Next
              </button>
            </form>
            <p>
              <button onClick={toggleForm} className="link-button">
                Don't have an account? Register here
              </button>
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

export default LoginPage;
