import React from "react";
import './channel_page.css';
import logo from './assets/logo2movfara2.png';

document.addEventListener("DOMContentLoaded", async () => {
  try {
    const response = await fetch("/api/auth/user", {
      method: "GET",
      credentials: "include",
    });
    if (response.ok) {
      const user = await response.json();
      document.getElementById("username").textContent = user.username;
      document.getElementById("email").textContent = user.email;
    } else {
      window.location.href = "/login_page.html";
    }
  } catch (err) {
    console.error("Error fetching user data:", err);
    window.location.href = "/login_page.html";
  }
});


function ChannelPage() {
  const handleCustomizeChannel = () => {
    alert('Funcționalitate: Personalizează canalul!');
  };

  const handleManageVideos = () => {
    alert('Funcționalitate: Gestionează videoclipurile!');
  };

  return (

    <div className="youtube-page">
      {/* Sidebar container including logo */}
      <div className="sidebar-container">
        <div className="logo-container">
         <img src={logo} alt="Logo" className="logo" />
         <span className="logo-text">TUBIFY</span>
        </div>
        <div className="sidebar">
          <ul className="menu">
            <li className="menu-item">Home</li>
            <li className="menu-item">Search</li>
            <li className="menu-item">Top Videos</li>
            <li className="menu-item">Account</li>
            <li className="menu-item">Settings</li>
          </ul>
        </div>
      </div>

      {/* Main Content */}
      <main className="content">
        <div className="channel-info">
          <div className="profile-pic">U</div>
          <div className="channel-details">
            <h2 id = "username"></h2>
            <p id="email"></p>
            <div className="buttons">
              <button onClick={handleCustomizeChannel}>Personalizează canalul</button>
              <button onClick={handleManageVideos}>Gestionează videoclipurile</button>
            </div>
          </div>
        </div>

        <div className="videos">
          <h3>Videoclipuri</h3>
          <div className="video-card">
            <img
              src="https://via.placeholder.com/300x150"
              alt="Videoclip"
              className="video-thumbnail"
            />
            <div className="video-info">
              <h4>
                COLEGIUL NAȚIONAL "ALEXANDRU VLAHUȚĂ" - PROMOȚIA 2019-2020
              </h4>
              <p>901 vizionări · acum 1 an</p>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default ChannelPage;
